.mcd-container {
    position: relative;
    margin: 0px auto;
    padding: 0px 0;
    clear: both;

}
@media only screen and (min-width: 1000px) {
    .container {
        width: 1000px;
    }
}

ul:last-child {
border-bottom: none;
}

.mcd-menu {
  list-style: none;
  padding: 0;
  margin: 0;
  background: #181B1F;
  font-size: 12px;
  height: 120px;
  border-radius: 1px;
  -moz-border-radius: 1px;
  -webkit-border-radius: 1px;
}
.mcd-menu li {
  list-style: none;
  float: left;
  position: relative;
  background: #181B1F;
}
.mcd-menu li a {
  display: block;
  text-decoration: none;
  padding: 5px 24px;
  text-align: center;
  color: #84C9FB;
}
.mcd-menu li a i {
  list-style: none;
  display: block;
  font-size: 50px;
  margin-bottom: 5px;
}
.mcd-menu li a strong {
  list-style: none;
  display: block;

}
.mcd-menu li a small {
  list-style: none;
  display: block;
  font-size: 12px;
}

.mcd-menu li a i, .mcd-menu li a strong, .mcd-menu li a small {
  list-style: none;
  position: relative;
   text-decoration: none;
  transition: all 300ms linear;
  -o-transition: all 300ms linear;
  -ms-transition: all 300ms linear;
  -moz-transition: all 300ms linear;
  -webkit-transition: all 300ms linear;
}
.mcd-menu li:hover > a i {
    opacity: 1;
    -webkit-animation: moveFromTop 300ms ease-in-out;
    -moz-animation: moveFromTop 300ms ease-in-out;
    -ms-animation: moveFromTop 300ms ease-in-out;
    -o-animation: moveFromTop 300ms ease-in-out;
    animation: moveFromTop 300ms ease-in-out;
}
.mcd-menu li:hover a strong {
    opacity: 1;
    -webkit-animation: moveFromLeft 300ms ease-in-out;
    -moz-animation: moveFromLeft 300ms ease-in-out;
    -ms-animation: moveFromLeft 300ms ease-in-out;
    -o-animation: moveFromLeft 300ms ease-in-out;
    animation: moveFromLeft 300ms ease-in-out;
}
.mcd-menu li:hover a small {
    opacity: 1;
    -webkit-animation: moveFromRight 300ms ease-in-out;
    -moz-animation: moveFromRight 300ms ease-in-out;
    -ms-animation: moveFromRight 300ms ease-in-out;
    -o-animation: moveFromRight 300ms ease-in-out;
    animation: moveFromRight 300ms ease-in-out;
}

.mcd-menu li:hover > a {
  color: #FEE75C;
}
.mcd-menu li a.active {
  list-style: none;
  position: relative;
  color: #84C9FB;
  border:0;
  border-top: 6px solid #84C9FB;
  border-bottom: 4px solid #84C9FB;
  margin-top: -4px;
  box-shadow: 0 0 5px #84C9FB;
  -moz-box-shadow: 0 0 5px #84C9FB;
  -webkit-box-shadow: 0 0 5px #84C9FB;
}
.mcd-menu li a.active:before {
  list-style: none;
  content: "";
  position: absolute;
  top: 0;
  left: 45%;
  border-top: 5px solid transparent;
  border-left: 5px solid transparent;
  border-right: 5px solid transparent;
}

@-webkit-keyframes moveFromTop {
    from {
        opacity: 0;
        -webkit-transform: translateY(200%);
        -moz-transform: translateY(200%);
        -ms-transform: translateY(200%);
        -o-transform: translateY(200%);
        transform: translateY(200%);
    }
    to {
        opacity: 1;
        -webkit-transform: translateY(0%);
        -moz-transform: translateY(0%);
        -ms-transform: translateY(0%);
        -o-transform: translateY(0%);
        transform: translateY(0%);
    }
}
@-webkit-keyframes moveFromLeft {
    from {
        opacity: 0;
        -webkit-transform: translateX(200%);
        -moz-transform: translateX(200%);
        -ms-transform: translateX(200%);
        -o-transform: translateX(200%);
        transform: translateX(200%);
    }
    to {
        opacity: 1;
        -webkit-transform: translateX(0%);
        -moz-transform: translateX(0%);
        -ms-transform: translateX(0%);
        -o-transform: translateX(0%);
        transform: translateX(0%);
    }
}
@-webkit-keyframes moveFromRight {
    from {
        opacity: 0;
        -webkit-transform: translateX(-200%);
        -moz-transform: translateX(-200%);
        -ms-transform: translateX(-200%);
        -o-transform: translateX(-200%);
        transform: translateX(-200%);
    }
    to {
        opacity: 1;
        -webkit-transform: translateX(0%);
        -moz-transform: translateX(0%);
        -ms-transform: translateX(0%);
        -o-transform: translateX(0%);
        transform: translateX(0%);
    }
}

.mcd-menu li ul,
.mcd-menu li ul li ul {
  list-style: none;
  position: absolute;
  height: auto;
  min-width: 200px;
  padding: 0;
  margin: 0;
  background: #181B1F;
  border-top: 4px solid #84C9FB;
  opacity: 0;
  visibility: hidden;
  transition: all 300ms linear;
  -o-transition: all 300ms linear;
  -ms-transition: all 300ms linear;
  -moz-transition: all 300ms linear;
  -webkit-transition: all 300ms linear;
  top: 130px;
  z-index: 1000;
}
.mcd-menu li ul:before {
  content: "";
  position: absolute;
  top: 0px;
  left: 23%;
  border-bottom: 5px solid transparent;
  border-left: 5px solid transparent;
  border-right: 5px solid transparent;
}
.mcd-menu li:hover > ul,
.mcd-menu li ul li:hover > ul {
  list-style: none;
  display: block;
  opacity: 1;
  visibility: visible;
  top: 100px;
}
.mcd-menu li ul li {
  float: none;
}
.mcd-menu li ul li a {
  list-style: none;
  padding: 6px;
  text-align: left;
  border: 0;
  border-bottom: 1px solid #84C9FB;
}
.mcd-menu li ul li a i {
  list-style: none;
  font-size: 14px;
  display: inline-block;
  margin: 0 10px 0 0;
}
.mcd-menu li ul li ul {
  list-style: none;
  left: 230px;
  top: 0;
  border: 0;
  border-left: 4px solid #84C9FB;
}  
.mcd-menu li ul li ul:before {
  list-style: none;
  content: "";
  position: absolute;
  top: 15px;
  left: 0px;
  border-right: 0px solid #84C9FB;
  border-bottom: 0px solid transparent;
  border-top: 0px solid transparent;
}
.mcd-menu li ul li:hover > ul {
  list-style: none;
  top: 0px;
  left: 200px;
}

.card-container * {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

.card-container {
  font-size: 100%;
  color: #fff;
  display: flex;
  justify-content: center;
  align-items: center;

  .card {
    background: #fefefe;
    color: #020202;
    padding: 1.8em 2.2em;
    width: 500px;
    border-radius: 20px;
    box-shadow: 12px 12px 30px rgba(0 0 0 / .33);

    p {
      margin-top: .7em;
      font-size: 1.125rem;
    }

    .nav {
      list-style: none;
      display: flex;
      justify-content: space-between;
      margin-top: 1em;

      li {
      margin-top: inherit;

        a {
          position: relative;
          display: inline-block;
          text-decoration: none;
          font-weight: 400;
          font-size: 1.125rem;
        }
      }
    }
  }
}

.card-container .card .link-1 {
  color: #000;
  background: linear-gradient(45deg, #f5aa4e, #f5aa4e);
  background-position: right;
  background-size: 0% 100%;
  background-repeat: repeat-y;
  transition: background-size 300ms ease-in-out;

   &:hover {
     background-size: 100% 100%;
     background-position: left;
  }
}

.card-container .card .link-2 {
  color: #000;
  background: linear-gradient(45deg, #f5aa4e, #f5aa4e);
  background-position: center bottom;
  background-size: 0% 4px;
  background-repeat: no-repeat;
  transition: background-size 300ms ease-in-out;

  &:hover {
    background-size: 100% 4px;
  }
}

.card-container .card .link-3 {
  color: #000;
  background: linear-gradient(45deg, #f5aa4e, #f5aa4e);
  background-position: bottom left;
  background-size: 0% 4px;
  background-repeat: no-repeat;
  transition: background-size 300ms ease-in-out;

  &:hover {
    background-size: 100% 4px;
  }
}

.card-container .card .link-4 {
  color: #000;
  background: linear-gradient(45deg, #f5aa4e, #f5aa4e);
  background-position: bottom;
  background-size: 100% 4px;
  background-repeat: repeat-x;
  transition: background-size 400ms ease-in-out;

  &:hover {
    background-size: 100% 50px;
  }
}
/* Scorecard */
.scorecard-wrapper {
  font-size: 1.25em;
  color: white;
border-radius: 6px;
}

.scorecard-wrapper ul {
padding-top: 8px;
  margin: 1em 0;
}

.scorecard-wrapper li {
  margin: 1em;
  margin-left: 1.5em;
font-weight: bold;
}
.scorecard-wrapper li .value {
  color: #84C9FB;
font-weight: normal;
}
.scorecard-wrapper ul, .scorecard-wrapper li {
  list-style: none;
}
.scorecard-wrapper a {
text-decoration: none;
color: inherit;
}
/* Timeline */
.container {
margin-top: 80px;
float: left;
}
.timeline,
.timeline-horizontal {
  list-style: none;
  padding: 20px;
  position: relative;
}
.timeline:before {
  top: 40px;
  bottom: 0;
  position: absolute;
  content: " ";
  width: 3px;
  background-color: #eeeeee;
  left: 50%;
  margin-left: -1.5px;
}
.timeline .timeline-item {
  margin-bottom: 20px;
  position: relative;
}
.timeline .timeline-item:before,
.timeline .timeline-item:after {
  content: "";
  display: table;
}
.timeline .timeline-item:after {
  clear: both;
}
.timeline .timeline-item .timeline-badge {
  color: #fff;
  width: 54px;
  height: 54px;
  line-height: 52px;
  font-size: 22px;
  text-align: center;
  position: absolute;
  top: 18px;
  left: 50%;
  margin-left: -25px;
  background-color: #333;
  border: 3px solid #ffffff;
  z-index: 100;
  border-top-right-radius: 50%;
  border-top-left-radius: 50%;
  border-bottom-right-radius: 50%;
  border-bottom-left-radius: 50%;
}
.timeline .timeline-item .timeline-timestamp {
position: absolute;
  bottom: 18px;
  left: 30%;
  z-index: 100;
}
.timeline .timeline-item .timeline-badge i,
.timeline .timeline-item .timeline-badge .fa,
.timeline .timeline-item .timeline-badge .glyphicon {
  top: 2px;
  left: 0px;
}
.timeline .open-color {
color: #57F287;
}
.timeline .closed-color {
color: #18242B;
}
.timeline .timeline-item .timeline-badge.OK {
  background-color: #57f287;
}
.timeline .timeline-item .timeline-badge.Warning {
  background-color: #5865f2;
}
.timeline .timeline-item .timeline-badge.Minor {
  background-color: #fee75c;
}
.timeline .timeline-item .timeline-badge.Major {
  background-color: #fa6400;
}
.timeline .timeline-item .timeline-badge.Critical {
  background-color: #ed4245;
}
.timeline .timeline-item .timeline-panel {
  position: relative;
  width: 46%;
  float: left;
  right: 16px;
  background: #F5F5F5;
  border-radius: 6px;
  -webkit-box-shadow: 0 1px 6px rgba(0, 0, 0, 0.175);
  box-shadow: 0 1px 6px rgba(0, 0, 0, 0.175);
overflow:hidden;
}
.timeline .timeline-item .timeline-panel:before {
  position: absolute;
  top: 26px;
  right: -16px;
  display: inline-block;
  border-top: 16px solid transparent;
  border-left: 16px solid #777;
  border-right: 0 solid #777;
  border-bottom: 16px solid transparent;
  content: " ";
}
.timeline .timeline-item .timeline-panel .timeline-title {
  margin-top: 0;
color: black;
}
.timeline .timeline-item .timeline-panel .primary-color{
color: #5865F2 !important;
}
.timeline .timeline-item .primary-outline {
border: 2px solid #5865F2;
} 
.timeline .timeline-item .timeline-panel .secondary-color {
color: #EB459E !important;
}
.timeline .timeline-item .secondary-outline {
border: 2px solid #EB459E;
}
.timeline .timeline-item .timeline-panel .timeline-body > p,
.timeline .timeline-item .timeline-panel .timeline-body > ul {
  margin-bottom: 0;
}
.timeline .timeline-item .timeline-panel .timeline-body > p + p {
  margin-top: 5px;
}
.timeline .timeline-item:last-child:nth-child(even) {
  float: right;
}
.timeline .timeline-item:nth-child(even) .timeline-panel {
  float: right;
  left: 16px;
}
.timeline .timeline-item:nth-child(even) .timeline-panel:before {
  border-left-width: 0;
  border-right-width: 14px;
  left: -14px;
  right: auto;
}
.timeline-horizontal {
  list-style: none;
  position: relative;
  padding: 20px 0px 20px 0px;
  display: inline-block;
}
.timeline-horizontal:before {
  height: 3px;
  top: auto;
  bottom: 26px;
  left: 56px;
  right: 0;
  width: 100%;
  margin-bottom: 20px;
}
.timeline-horizontal .timeline-item {
  display: table-cell;
  height: 280px;
  width: 20%;
  min-width: 320px;
  float: none !important;
  padding-left: 0px;
  padding-right: 20px;
  margin: 0 auto;
  vertical-align: bottom;
}
.timeline-horizontal .timeline-item .timeline-panel {
  top: auto;
  bottom: 64px;
  display: inline-block;
  float: none !important;
  left: 0 !important;
  right: 0 !important;
  width: 100%;
  margin-bottom: 20px;
}
.timeline-horizontal .timeline-item .timeline-panel:before {
  top: auto;
  bottom: -16px;
  left: 28px !important;
  right: auto;
  border-right: 16px solid transparent !important;
  border-top: 16px solid #777 !important;
  border-bottom: 0 solid #777 !important;
  border-left: 16px solid transparent !important;
}
.timeline-horizontal .timeline-item:before,
.timeline-horizontal .timeline-item:after {
  display: none;
}
.timeline-horizontal .timeline-item .timeline-badge {
  top: auto;
  bottom: 0px;
  left: 43px;
}
.timeline .timeline-item .timeline-panel p {
color: black;
font-weight: bold;
margin: 0;
padding: 6px;
}
.timeline .timeline-item .timeline-panel p:nth-child(even) {
background-color: #84C9FB;
}
.timeline .timeline-item .timeline-panel p .value {
font-weight: normal;
font-size: 1rem;
}
.timeline .timeline-item .timeline-panel .title {
text-align: center;
font-size: 1.3rem;
color: black;
}
.timeline .timeline-item .timeline-panel .timeline-title-group {
color: black;
font-size: 1rem;
}
.timeline .text-bold {
font-weight: bold;
}
.timeline .timeline-heading {
display: flex;
flex-direction: row;
flex-wrap: nowrap;
justify-content: space-between;
}
.timeline .larger-icon {
font-size: x-large;
}
